mapper.select:
select

from wms_storage_plane
where 1 = 1

  AND storage_plane_id = #{storagePlaneId,jdbcType=VARCHAR}


  AND warehouse_code = #{warehouseCode,jdbcType=VARCHAR}


  AND area_code = #{areaCode,jdbcType=VARCHAR}


  AND location_code = #{locationCode,jdbcType=VARCHAR}


  AND location_attr = #{locationAttr,jdbcType=VARCHAR}


  AND location_desc = #{locationDesc,jdbcType=VARCHAR}


  AND goods_code = #{goodsCode,jdbcType=VARCHAR}


  AND batch_no = #{batchNo,jdbcType=VARCHAR}


  AND available_amount = #{availableAmount,jdbcType=INTEGER}


  AND lock_amount = #{lockAmount,jdbcType=INTEGER}


  AND user_defined1 = #{userDefined1,jdbcType=VARCHAR}


  AND user_defined2 = #{userDefined2,jdbcType=VARCHAR}


  AND user_defined3 = #{userDefined3,jdbcType=VARCHAR}


  AND user_defined4 = #{userDefined4,jdbcType=VARCHAR}


  AND user_defined5 = #{userDefined5,jdbcType=VARCHAR}


  AND create_by = #{createBy,jdbcType=VARCHAR}


  AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


  AND last_modified_by = #{lastModifiedBy,jdbcType=VARCHAR}


  AND gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


  AND active_flag = #{activeFlag,jdbcType=CHAR}
