mapper.select:
select

from wcs_agv_task_plane
where 1=1

  and path_id = #{pathId,jdbcType=INTEGER}


  and task_status=#{taskStatus,jdbcType=CHAR}


  and task_no=#{taskNo,jdbcType=INTEGER}


  and pallet_code = #{palletCode,jdbcType=VARCHAR}


  and user_defined1=#{userDefined1,jdbcType=VARCHAR}


  and user_defined2=#{userDefined2,jdbcType=VARCHAR}


  and user_defined3=#{userDefined3,jdbcType=VARCHAR}

order by priority

select from wcs_agv_task_plane
where priority>0 AND task_status=#{entity.taskStatus,jdbcType=CHAR}
order by priority

