mapper.insert:
INSERT INTO wms_order_check_pallet ( check_id, order_no, goods_name, goods_code, location_code, diff_flag, batch_no, pallet_code, amount, real_amount, remark, user_defined1, user_defined2, create_by, gmt_create, active_flag ) SELECT #{entity.checkId}, #{entity.orderNo}, s.goods_name, t.goods_code, t.location_code, 1, t.batch_no, t.pallet_code, t.amount, 0, #{entity.remark}, 0, #{entity.userDefined2}, #{entity.createBy}, #{entity.gmtCreate}, 1 FROM wms_pallet t JOIN wms_location_stereoscopic n ON t.location_code = n.location_code LEFT JOIN wms_goods s ON t.goods_code = s.goods_code WHERE t.active_flag = '1' AND ISNULL(t.lock_by,'') = ''
AND t.gmt_modified > #{entity.gmtModified}
AND t.location_code = #{entity.locationCode}
AND t.goods_code = #{entity.goodsCode}
AND t.area_code = #{entity.areaCode}

